import java.util.*;
import java.io.*;

public class Task &#123;

    public static final int N = 8;

    public static String ArrayName[] = &#123;"武旭华", "闫奥威", "王锐", "宁圣泽", "王鹏飞", "王琳", "张俊锋", "王鹏"&#125;;
    public static int ArrayAge[] = &#123;20, 20, 21, 20, 20, 21, 21, 21&#125;;
    public static float ArrayHeight[] = &#123;170f, 170f, 175f, 170f, 150f, 170f, 171f, 180f&#125;;
    public static double ArrayWeight[] = &#123;107, 130, 120, 120, 140, 140, 140, 160&#125;;
    public static String ArrayStudentNumber[] = &#123;"2014127013", "2014127001", "2014127002", "2014127003", "2014127004", "2014127008", "2014127011", "2014127012"&#125;;

    public static void main(String[] args) &#123;

        System.out.println("分别用四种方式输出一个宿舍内所有人的属性：");

        method1();
        method2();
        method3();
        method4();

    &#125;

    public static void method1() &#123;

        System.out.println("第一种方法（对象）：");

        People P0 = new People("武旭华", 20, 170f, 107);
        People P1 = new People("闫奥威", 20, 170f, 130);
        People P2 = new People("王锐", 20, 175f, 120);
        People P3 = new People("宁圣泽", 20, 170f, 120);
        People P4 = new People("王鹏飞", 21, 150f, 140);
        People P5 = new People("王琳", 21, 170f, 140);
        People P6 = new People("张俊锋", 21, 170f, 140);
        People P7 = new People("王鹏", 21, 180f, 160);

        System.out.println(P0);
        System.out.println(P1);
        System.out.println(P2);
        System.out.println(P3);
        System.out.println(P4);
        System.out.println(P5);
        System.out.println(P6);
        System.out.println(P7);

    &#125;

    public static void method2() &#123;

        System.out.println("第二种方法（数组）：");

        People P[] = new People[N];

        for (int i = 0; i &lt; P.length; i++) &#123;
            P[i] = new People(ArrayName[i], ArrayAge[i], ArrayHeight[i], ArrayWeight[i]);
        &#125;

        for (int i = 0; i &lt; P.length; i++) &#123;
            System.out.println(P[i]);
        &#125;

    &#125;

    public static void method3() &#123;

        System.out.println("第三种方法（集合）：");

        System.out.println("————list————");

        List&lt;People> PL = new ArrayList&lt;>();

        for (int i = 0; i &lt; N; i++) &#123;
            People PP = new People(ArrayName[i], ArrayAge[i], ArrayHeight[i], ArrayWeight[i]);
            PL.add(PP);
        &#125;

        for (People P : PL) &#123;
            System.out.println(P);
        &#125;

        System.out.println("————set————");

        Set&lt;People> PS = new HashSet&lt;>();

        for (int i = 0; i &lt; N; i++) &#123;
            People PP = new People(ArrayName[i], ArrayAge[i], ArrayHeight[i], ArrayWeight[i]);
            PS.add(PP);
        &#125;

        for (People P : PS) &#123;
            System.out.println(P);
        &#125;

        System.out.println("————map————");

        Map&lt;String, People> PM = new HashMap&lt;>();

        for (int i = 0; i &lt; N; i++) &#123;
            People PP = new People(ArrayName[i], ArrayAge[i], ArrayHeight[i], ArrayWeight[i]);
            PM.put(ArrayStudentNumber[i], PP);
        &#125;

        for (int i = 0; i &lt; N; i++) &#123;
            System.out.println(PM.get(ArrayStudentNumber[i]));
        &#125;

    &#125;

    public static void method4() &#123;

        System.out.println("第四种方法（流）：");

        File file = new File("E:\\Java\\src\\text.txt");
        BufferedReader reader = null;
        try &#123;
            System.out.println("以行为单位读取文件内容，一次读一整行：");
            reader = new BufferedReader(new FileReader(file));
            String tempString = null;
            int line = 1;
            // 一次读入一行，直到读入null为文件结束
            while ((tempString = reader.readLine()) != null) &#123;
                // 显示行号
                System.out.println("line " + line + ": " + tempString);
                line++;
            &#125;
            reader.close();
        &#125; catch (IOException e) &#123;
            e.printStackTrace();
        &#125; finally &#123;
            if (reader != null) &#123;
                try &#123;
                    reader.close();
                &#125; catch (IOException e1) &#123;
                &#125;
            &#125;
        &#125;
    &#125;

